; ml /c /coff HelloWord.asm; link /subsystem:windows Hello.obj;>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>; 模式定义;>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>        .386 ; 指定使用的指令集		.model flat,stdcall ; 定义程序工作模式		option casemap:none ; 大小写敏感		;>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>; Include 文件定义;>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>include		windows.incinclude    	user32.incincludelib 	user32.libinclude    	kernel32.incincludelib 	kernel32.lib;>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>; 数据段;>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>		.dataszCaption	db	'Hello',0szText		db	'HelloWord!',0;>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>; 代码段;>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>		.codestart:		invoke MessageBox,\		NULL,\		offset szText,\		offset szCaption,\		MB_OK				invoke ExitProcess,NULL;>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>		end start