<?	session_start();		$conn = mysql_connect('localhost', 'root', '') or die("服务器连接错误");	mysql_select_db('member', $conn) or die("数据库连接错误");		// 定义常量	define(ALL_PS, "MINGZI");		function user_shell($uid,$shell,$m_id)	{		$sql="SELECT * FROM `user_list` WHERE `uid` = '$uid'";		$query=mysql_query($sql); echo $query;		$us=is_array($row=mysql_fetch_array($query));		$shell = $us ? $shell == md5($row['username'].$row['password'].ALL_PS):FALSE;		if($shell)		{			if($row['m_id']<=$m_id)			{				return $row;			}			else			{				echo "你的权限不足<br>";				exit();			}		}		else		{			echo "你无权限访问该页<br>";			exit();		}	}		function user_mktime($onlinetime)	{		$new_time=mktime();		if($new_time-$onlinetime > '3600')		{			echo "登陆超时<br>";			exit();			session_destroy();		}		else		{			$_SESSION['times']=mktime();		}	}?>