<p style="color:red"><u>cookie</u></p><hr><p>	setcookie(string name, string value, int expire, string path, string domain, int secure);<br>	-----------名称必须-------值--必须--------时间------保存路径-----保存的域----------HTTPS  <br>	<br>	echo $MyCookie;<br>	echo $CookieArray[0];<br>	echo $_COOKIE["MyCookie"];<br>	echo $HTTP_COOKIE_VARS["MyCookie"];<br>	<br>	删除Cookie<br>	setcookie("Cookie","");<br>	SetCookie("Cookie","value", time()-1/time());<br>	<br>	Cookie的限制<br>	............<br></p><hr><p style="color:blue"><u><i>session</i></u></p><hr><p>	session_start();		// 初始化session 需在文件头部<br>	S_SESSION[name]=value;	// 配置session<br>	echo $_SESSION[name];	// 使用session<br>	isset($_SESSION[name]);	// 判断<br>	unset($_SESSION[name]);	// 删除<br>	session_destroy();		// 销毁所有session<br>	<br>	php5下不再使用的有：<br>		session_register();<br>		session_unregister();<br>		session_is_registered();<br>		等<br>	<br></p><hr><p><u><i><b>应用----登陆---cookie----</b></i></u></p><hr><?	if($_GET['out'])	{		setcookie('id','');		setcookie('pass','');		echo "<script>location.href='cookie_session.php'</script>";	}	if($_POST['name'] && $_POST['password']) // 判断登陆	{		setcookie('id',$_POST['name'], time()+3600);		setcookie('pass',$_POST['password'], time()+3600);				echo "<script>location.href='cookie_session.php'</script>"; // 用于刷新一下	}	if($_COOKIE['id'] && $_COOKIE['pass'])	{		echo "登陆成功<br>用户名：".$_COOKIE['id']."<br>密码：".$_COOKIE['pass']."<br>";				echo "<a href='cookie_session.php?out=out'>退出</a>";	}?><form action="cookie_session.php" method="post">	用户ID:	<input type="text" name="name">	<br><br>	密码：	<input type="password" name="password">	<br><br>	<input type="submit" value="登陆"></form><hr><p><u><i><b>应用----登陆---session----</b></i></u></p><hr><?	session_start();		if($_GET['out1'])	{		unset($_SESSION['id']);		unset($_SESSION['pass']);	}		if($_POST['name1'] && $_POST['password1']) // 判断登陆	{		$_SESSION['id'] = $_POST['name1'];		$_SESSION['pass'] = $_